    /**
    * Translate English word into Hieroglyphics
    *      
    * @param string $word English word
    *      
    * @return resource Image resource identifier
    * @author Khaled Al-Shamaa <khaled.alshamaa@gmail.com>
    */
    public function en2hiero($word){
        $word = strtoupper($word);
        $word = str_replace('TH', '1', $word);
        $chars = preg_split('//', $word, -1, PREG_SPLIT_NO_EMPTY);
        
        $total_w = 0;
        $max_h   = 0;
        
        foreach ($chars as $char) {
            $total_w += $this->font[$char]['w'];
            if ($this->font[$char]['h']>$max_h) { 
                $max_h = $this->font[$char]['h']; 
            }
        }
        
        $im  = imagecreatetruecolor($total_w, $max_h);
        $bck = imagecolorallocate($im, 255,255,255);
        imagefill($im, 0, 0, $bck);
        
        $current_x = 0;
        
        foreach ($chars as $char) {
            $image = imagecreatefromstring(base64_decode($this->font[$char]['image']));
            imagecopy ($im, $image, $current_x, $max_h-$this->font[$char]['h'], 
                       0, 0, $this->font[$char]['w'], $this->font[$char]['h']);
            $current_x += $this->font[$char]['w'];
        }
        
        return $im;
    }
